  {
    GenLib.removeTranlationRows(getCrsIncomingDocumentsTl());
    super.remove();
  }
  
  protected void doDML(int operation, oracle.jbo.server.TransactionEvent e) {
    if (operation == DML_INSERT && getIncomingDocumentsNo()==null) {
      setIncomingDocumentsNo(getMaxSerialForIncomingDocumentNo());
      setBarcode(getMaxSerialForIncomingDocumentNo());
      }
    super.doDML(operation, e);
    if (operation == DML_INSERT) {
      BigDecimal maxCrsIncomingDocumentNumber = GenLib.getPrimaryKeyValue(getDBTransaction(), "CRS_INCOMING_DOCUMENTS_SEQ");
      setIncomingDocumentsPk(maxCrsIncomingDocumentNumber);
      insertIntoCrsIncomingDocumentsTranslation();
      insertIntoIncomingDocCcFromDocClass();
    }
  }
  private BigDecimal getMaxSerialForIncomingDocumentNo() {
    DBTransaction dbt = getDBTransaction();
    BigDecimal maxIncomingDocumentNumber = new BigDecimal(0);
    BigDecimal crsNumberingType = Settings.getSettings().getCrsNumberingTypeFk();
    BigDecimal employerLevel  =  BigDecimal.valueOf(Constants.EMPLOYER_LEVEL);
    BigDecimal departmentLevel  =  BigDecimal.valueOf(Constants.DEPARTMENT_LEVEL);
    if (crsNumberingType.equals((employerLevel))) {
      maxIncomingDocumentNumber =GenLib.getNextMax(dbt, "CRS_INCOMING_DOCUMENTS", "INCOMING_DOCUMENTS_NO",
                          "where IN_DOC_YEAR=" + getInDocYear());
    }
    if (crsNumberingType.equals(departmentLevel)){
      BigDecimal UserLoginDepartment = gen.common.GenLib.getUserLoginDepartment();
      maxIncomingDocumentNumber =GenLib.getNextMax(dbt, "CRS_INCOMING_DOCUMENTS", "INCOMING_DOCUMENTS_NO",
                          "where USER_DEPARTMENT_FK= " + UserLoginDepartment + " and IN_DOC_YEAR=" +
                          getInDocYear());
    }
    return maxIncomingDocumentNumber    ;
  }
  private void insertIntoCrsIncomingDocumentsTranslation  () {
   oracle.jbo.server.EntityDefImpl crsIncomingDocumentsTlDefinition = CrsIncomingDocumentsTlImpl.getDefinitionObject();
    java.util.List<Integer> activeLanguages = gen.common.GenLib.getActiveDataLanguages(getDBTransaction());
      for (Integer languageId : activeLanguages) {
    CrsIncomingDocumentsTlImpl crsIncomingDocumentsTlImpl = (CrsIncomingDocumentsTlImpl) crsIncomingDocumentsTlDefinition.createInstance2(getDBTransaction(), null);
    crsIncomingDocumentsTlImpl.setLanguagesFk(new BigDecimal(languageId));
    crsIncomingDocumentsTlImpl.setInSubject(getDisplayInSubject());
    crsIncomingDocumentsTlImpl.setKeyword(getDisplayKeyword());
    crsIncomingDocumentsTlImpl.setNotes(getDisplayNotes());
    crsIncomingDocumentsTlImpl.setIncomingDocumentsFk(getIncomingDocumentsPk());
    }
  }
  
  private void insertIntoIncomingDocCcFromDocClass(){
   if (getDistributionTypeFk() != null && getDistributionTypeFk().equals(new BigDecimal(Constants.AUTO_ASSIGN))&&
       getClassificationFk() != null && getIncomingDocumentsPk() != null){
       ControllerUtil.insertIncomingDocCcFromDocClass(getIncomingDocumentsPk(), getClassificationFk()); 
   }
   }
