  protected void create(AttributeList attributeList) {
    super.create(attributeList);
    
    setOutDocYear(GenLib.getCurrentYear());
    setOutDocDateG(GenLib.getCurrentDate());
    setOutDocDateH(HijriUtil.getTodayHijriBigDecimalWithDate());
    setFromDepartmentFk(GenLib.getUserLoginDepartment());
    setUserDepartmentFk(GenLib.getUserLoginDepartment());
  }

  protected void doDML(int operation, oracle.jbo.server.TransactionEvent e)
  {
    if (operation == DML_INSERT){
      setOutgoingDocumentsNo(generateOutgoingDocumentsNo());
      setBarcode(generateOutgoingDocumentsNo());
    }
    super.doDML(operation, e);
    if (operation == DML_INSERT){
      BigDecimal outgoingDocumentsNoPreparationpk = GenLib.getPrimaryKeyValue(getDBTransaction(), "CRS_OUTGOING_DOC_PREPARATION_SEQ");
      setOutgoingDocPreparationPk(outgoingDocumentsNoPreparationpk);
      insertToOutgoingDocumentPreparationTl();
    }
  }
  
  private BigDecimal generateOutgoingDocumentsNo() {
  DBTransaction databaseTransaction = getDBTransaction();
  BigDecimal outgoingDocumentsNo = GenLib.getNextMax(databaseTransaction, "CRS_OUTGOING_DOC_PREPARATION", "OUTGOING_DOCUMENTS_NO",
                                   "where OUT_DOC_YEAR=" + getOutDocYear());
  return outgoingDocumentsNo;
  
  }
  
  private void insertToOutgoingDocumentPreparationTl(){ 
  
      oracle.jbo.server.EntityDefImpl outgoingDocPreparationdefinition = CrsOutgoingDocPreTlImpl.getDefinitionObject();
      List<Integer> activeLanguages = GenLib.getActiveDataLanguages(getDBTransaction());
       
   for (Integer languageId : activeLanguages){    
        CrsOutgoingDocPreTlImpl outgoingDocumentPreparationTl = (CrsOutgoingDocPreTlImpl) outgoingDocPreparationdefinition.createInstance2(getDBTransaction(), null);
        outgoingDocumentPreparationTl.setLanguagesFk(BigDecimal.valueOf(languageId));
        outgoingDocumentPreparationTl.setOutSubject(getDisplayOutSubject());
        outgoingDocumentPreparationTl.setOutgoingDocPreparationFk(getOutgoingDocPreparationPk());
        outgoingDocumentPreparationTl.setKeyword(getDisplayKeyword());
        outgoingDocumentPreparationTl.setNotes(getDisplayNotes());
      }
    }
   
